import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import sessionsService from "./sessionsService";

const initialState = {
  trainingSession: {
    title: "",
    description: "",
  },
  sessionBank: [],
  trainingPlan: [],
  isError: false,
  isSuccess: false,
  isLoading: false,
  message: "",
};

export const getSessions = createAsyncThunk(
  "sessions/getAll",
  async (_, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;
      return await sessionsService.getSessions(token);
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const addSession = createAsyncThunk(
  "sessions/add",
  async (sessionData, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;
      return await sessionsService.addSession(sessionData, token);
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const deleteSession = createAsyncThunk(
  "sessions/delete",
  async (id, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;
      return await sessionsService.deleteSession(id, token);
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const updateSession = createAsyncThunk(
  "sessions/updateSession",
  async (session, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;
      return await sessionsService.updateSession(session, token);
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const clearTrainingPlan = createAsyncThunk(
  "sessions/clearTrainingPlan",
  async (_, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;
      return await sessionsService.clearTrainingPlan(token);
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const sessionsSlice = createSlice({
  name: "sessions",
  initialState,
  reducers: {
    reset: (state) => initialState,
    changeCurrentSession: (state, action) => {
      const { key, value } = action.payload;
      state.trainingSession = { ...state.trainingSession, [key]: value };
    },

    dragSession: (state, action) => {
      const { sourceId, sourceIndex, destinationIndex, destinationId } =
        action.payload;

      if (sourceId === "training-plan-key") {
        const removedSession = state.trainingPlan.splice(sourceIndex, 1);
        if (destinationId === "training-plan-key") {
          state.trainingPlan.splice(destinationIndex, 0, ...removedSession);
          state.trainingPlan[destinationIndex] = {
            ...state.trainingPlan[destinationIndex],
            inPlan: true,
            index: destinationIndex,
          };
          state.trainingPlan[sourceIndex] = {
            ...state.trainingPlan[sourceIndex],
            inPlan: true,
            index: sourceIndex,
          };
        } else {
          state.sessionBank.splice(destinationIndex, 0, ...removedSession);
          state.sessionBank[destinationIndex] = {
            ...state.sessionBank[destinationIndex],
            inPlan: false,
            index: destinationIndex,
          };
          state.trainingPlan[sourceIndex] = {
            ...state.trainingPlan[sourceIndex],
            inPlan: true,
            index: sourceIndex,
          };
        }
      } else {
        const removedSession = state.sessionBank.splice(sourceIndex, 1);
        if (destinationId === "training-plan-key") {
          state.trainingPlan.splice(destinationIndex, 0, ...removedSession);
          state.trainingPlan[destinationIndex] = {
            ...state.trainingPlan[destinationIndex],
            inPlan: true,
            index: destinationIndex,
          };
          state.sessionBank[sourceIndex] = {
            ...state.sessionBank[sourceIndex],
            index: sourceIndex,
          };
        } else {
          state.sessionBank.splice(destinationIndex, 0, ...removedSession);
          state.sessionBank[destinationIndex] = {
            ...state.sessionBank[destinationIndex],
            index: destinationIndex,
          };
          state.sessionBank[sourceIndex] = {
            ...state.sessionBank[sourceIndex],
            index: sourceIndex,
          };
        }
      }
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(getSessions.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getSessions.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        const sessions = action.payload;
        state.sessionBank = sessions.filter((session) => !session.inPlan);
        state.trainingPlan = sessions.filter((session) => session.inPlan);
      })
      .addCase(getSessions.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(addSession.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(addSession.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.sessionBank.push(action.payload);
        state.trainingSession = {
          title: "",
          description: "",
        };
      })
      .addCase(addSession.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(deleteSession.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(deleteSession.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.sessionBank = state.sessionBank.filter(
          (session) => session._id !== action.payload.id
        );
      })
      .addCase(deleteSession.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(updateSession.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(updateSession.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        const { inPlan, _id, completed, type } = action.payload;
        if (type === "change-plan") {
          if (inPlan) {
            state.sessionBank = state.sessionBank.filter(
              (session) => session._id !== _id
            );
            delete action.payload.type;
            state.trainingPlan.push(action.payload);
          } else {
            state.trainingPlan = state.trainingPlan.filter(
              (session) => session._id !== _id
            );
            delete action.payload.type;
            state.sessionBank.push(action.payload);
          }
        } else if (type === "change-completed") {
          state.trainingPlan = state.trainingPlan.map((session) =>
            session._id === _id
              ? { ...session, completed: !session.completed }
              : session
          );
        }
      })
      .addCase(updateSession.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(clearTrainingPlan.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(clearTrainingPlan.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.sessionBank = action.payload;
        state.trainingPlan = [];
      })
      .addCase(clearTrainingPlan.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      });
  },
});

export const currentSession = (state) => state.sessions.trainingSession;
export const sessionBank = (state) => state.sessions.sessionBank;
export const sessionPlan = (state) => state.sessions.trainingPlan;

export const { changeCurrentSession, dragSession, reset } =
  sessionsSlice.actions;

export default sessionsSlice.reducer;
