import React from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import {
  removeSessionFromPlan,
  deleteSession,
} from "../../../../../redux/sessions/sessionsSlice";
import { ReactComponent as ChevronRight } from "../../../../../assets/chevron-right.svg";
import { updateSession } from "../../../../../../../server/controllers/sessionController";

const StyledButton = styled.button`
  font-size: 20px;
  border: none;
  width: 60px;
  background-color: #e7a957;
  display: flex;
  align-items: center;
  justify-content: center;
  border-left: 3px solid #c84461;
  & > svg {
    height: 30px;
  }
  &:hover {
    cursor: pointer;
    transition: background-color 0.1s ease-in;

    &.add {
      background-color: #2cad6d;
    }
    &.remove {
      background-color: #e91e63;
    }
  }
`;

export default function SessionButtons({ list, session }) {
  const dispatch = useDispatch();
  const addToPlan = () => dispatch(updateSession(session));
  const removeFromPlan = () => dispatch(removeSessionFromPlan(session));
  const removeFromBank = () => dispatch(deleteSession(session._id));

  return list === "bank" ? (
    <div className="button-container">
      <StyledButton className="remove" onClick={removeFromBank}>
        X
      </StyledButton>
      <StyledButton className="add" onClick={addToPlan}>
        <ChevronRight />
      </StyledButton>
    </div>
  ) : (
    <div className="button-container">
      <StyledButton className="remove" onClick={removeFromPlan}>
        X
      </StyledButton>
      <StyledButton className="add">OK</StyledButton>
    </div>
  );
}
