import React from "react";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { ReactComponent as NovosLogo } from "../../../../assets/novos.svg";
import { currentUser } from "../../../../redux/login/loginSlice";

const NavContainer = styled.nav`
  height: 70px;
  padding: 12px 80px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #635164;
  & > .right-side {
    display: flex;
    align-items: center;
    gap: 24px;
    color: white;

    & > .logout-button {
      & > button {
        border: none;
        font-size: 16px;
        border-radius: 4px;
        padding: 8px 10px;
        background-color: #433558;
        color: #745e76;
        opacity: 0.8;
        font-weight: 600;
        &:hover {
          cursor: pointer;
          opacity: 1;
          transition: opacity 0.1s ease-in;
        }
      }
    }

    & > .user-details {
      display: flex;
      gap: 4px;
    }
    & > .name-bubble {
      height: 60px;
      width: 60px;
      display: flex;
      justify-content: center;
      align-items: center;
      font-size: 30px;
      border-radius: 50%;
      background-color: #c82879;
      letter-spacing: 1px;
    }
  }
`;

export default function Nav() {
  const user = useSelector(currentUser);
  return (
    <NavContainer>
      <NovosLogo />
      <div className="right-side">
        <div className="user-details">
          <h3>{user.name}</h3>
          <h3>{user.lastName}</h3>
        </div>
        <div className="logout-button">
          <button>Log out</button>
        </div>
        <div className="name-bubble">
          {user.name[0]}
          {user.lastName[0]}
        </div>
      </div>
    </NavContainer>
  );
}
