import { createSlice } from "@reduxjs/toolkit";
import { v4 as uuidv4 } from "uuid";

export const trainingManagerSlice = createSlice({
  name: "trainingManager",
  initialState: {
    trainingSession: {
      title: "",
      description: "",
    },
    trainingSessionsBank: [
      {
        title: "testSession1",
        description: "you go and do this and that and stuff happens",
        id: "1243asd1",
        index: 0,
      },
      {
        title: "testSession2",
        description: "you go and do this and that and stuff happens",
        id: "21235q45",
        index: 1,
      },
      {
        title: "testSession3",
        description: "you go and do this and that and stuff happens",
        id: "312312asfd4",
        index: 2,
      },
    ],
    trainingPlan: [
      {
        title: "testSession4",
        description: "you go and do this and that and stuff happens",
        id: "1123q",
        index: 0,
      },
      {
        title: "testSession5",
        description: "you go and do this and that and stuff happens",
        id: "242124asda",
        index: 1,
      },
      {
        title: "testSession6",
        description: "you go and do this and that and stuff happens",
        id: "3312aw",
        index: 2,
      },
    ],
  },
  reducers: {
    addSessionToBank: (state, action) => {
      state.trainingSessionsBank = [
        ...state.trainingSessionsBank,
        {
          ...action.payload,
          id: uuidv4(),
          index: state.trainingSessionsBank.length,
        },
      ];
    },
    removeSessionFromBank: (state, action) => {
      state.trainingSessionsBank = state.trainingSessionsBank.filter(
        (session) => session.id !== action.payload
      );
    },
    addSessionToPlan: (state, action) => {
      state.trainingSessionsBank = state.trainingSessionsBank.filter(
        (session) => session.id !== action.payload.id
      );
      state.trainingPlan = [...state.trainingPlan, action.payload];
    },
    removeSessionFromPlan: (state, action) => {
      state.trainingPlan = state.trainingPlan.filter(
        (session) => session.id !== action.payload.id
      );
      state.trainingSessionsBank = [
        ...state.trainingSessionsBank,
        action.payload,
      ];
    },
    changeCurrentSession: (state, action) => {
      const { key, value } = action.payload;
      state.trainingSession = { ...state.trainingSession, [key]: value };
    },
    clearTrainingPlan: (state) => {
      state.trainingSessionsBank = [
        ...state.trainingSessionsBank,
        ...state.trainingPlan,
      ];
      state.trainingPlan = [];
    },
    dragSession: (state, action) => {
      const { sourceId, sourceIndex, destinationIndex, destinationId } =
        action.payload;
      if (sourceId === "training-plan-key") {
        const removedSession = state.trainingPlan.splice(sourceIndex, 1);
        if (destinationId === "training-plan-key") {
          state.trainingPlan.splice(destinationIndex, 0, ...removedSession);
        } else {
          state.trainingSessionsBank.splice(
            destinationIndex,
            0,
            ...removedSession
          );
        }
      } else {
        const removedSession = state.trainingSessionsBank.splice(
          sourceIndex,
          1
        );
        if (destinationId === "training-plan-key") {
          state.trainingPlan.splice(destinationIndex, 0, ...removedSession);
        } else {
          state.trainingSessionsBank.splice(
            destinationIndex,
            0,
            ...removedSession
          );
        }
      }
    },
  },
});

export const currentSession = (state) => state.trainingManager.trainingSession;
export const sessionBank = (state) =>
  state.trainingManager.trainingSessionsBank;
export const sessionPlan = (state) => state.trainingManager.trainingPlan;

export const {
  addSessionToBank,
  removeSessionFromBank,
  addSessionToPlan,
  removeSessionFromPlan,
  changeCurrentSession,
  clearTrainingPlan,
  dragSession,
} = trainingManagerSlice.actions;

export default trainingManagerSlice.reducer;
