import { createSlice } from "@reduxjs/toolkit";

export const loginSlice = createSlice({
  name: "loginManager",
  initialState: {
    activeUser: {
      name: "Yanay",
      lastName: "Lax",
    },
  },
  reducers: {},
});

export const currentUser = (state) => state.loginManager.activeUser;

export default loginSlice.reducer;
